                        }

                        splitIcon = poi.icon.Split('/');
                        splitIcon = splitIcon[splitIcon.Length - 1].Split('-');

                        if(splitIcon[0] == "generic_business")
                        {
                            poi.typePOI = findTypeByName(poi.name);
                        }
                        else
                        {
                            poi.typePOI = formatType(splitIcon[0]);
                        }
                        

                        days[i].Add(poi);
                    }
                }
            }

            if(days != null && days.Length != 0)
            {
                variables.days = days;
                SceneManager.LoadScene("Scene1");
            }
            else
            {
                PlayerPrefs.SetString("errorDatosServicio", "The requested information could not be generated, try again");
                Screen.orientation = ScreenOrientation.Portrait;
                SceneManager.LoadScene("SceneForm");
            }
        }
    }

    private string findTypeByName(string name)
    {
        name = name.ToLower();
        string[] words = name.Split(' ');

        for(int i = 0; i < words.Length; i++)
        {
            switch (words[i])
            {
                case "museo":
                    return "Museum";
                case "restaurante":
                    return "Restaurant";
                case "acuario":
                    return "Aquarium";
                case "cementerio":
                    return "Cementery";
                case "mesquita":
                    return "Mosque";
                case "galeria de arte":
                    return "Museum";
                case "estadio":
                    return "Stadium";
                case "iglesia":
                    return "Church";
                case "parque de atracciones":
                    return "Amusement";
                case "templo hindu":
                    return "Temple";
                case "zoologico":
                    return "Zoo";
                case "parque":
                    return "Park";
                case "hotel":
                    return "Hotel";
            }
        }
        
        return "Default point of interest";
    }

    private string formatType(string type)
    {

        switch (type)
        {
            case "museum":
                return "Museum";
            case "restaurant":
                return "Restaurant";
            case "shopping":
                return "Shopping";
            case "cementery_grave":
                return "Cementery";
            case "worship_islam":
                return "Mosque";
            case "stadium":
                return "Stadium";
            case "worship_general":
                return "Church";
            case "park":
                return "Park";
            case "worship_hindu":
                return "Hindu Temple";
            case "hotel":
                return "Hotel";

            default:
                return "Default point of interest";
        }
    }


